sysevent_script_action.script:
/**
 * Does the work on each loop.
 * @param {Number} [limit=10] - The number of records to process per loop.
 * @param {Number} [currentNumber=0] - The number of records that have been processed so far, by all previous loops.
 */
function eventWrapper(limit, currentNumber) {

    var EVENT_NAME = 'x_792082_ytt.generate.video.transcript'; //todo: Update this to the name of the event you've created.
    var TABLE_NAME = 'x_792082_ytt_video'; //todo: Update this to the name of the table containing the records you're processing
    var QUERY = 'transcriptISEMPTY'; //todo: Put your query here

    var grRec = new GlideRecord(TABLE_NAME);

    //Set default values
    limit = Number(limit) > 0 ? Number(limit) : 10; //Default: 10
    currentNumber = currentNumber > 0 ? currentNumber : 0; //Default: 0

    grRec.setLimit(limit);
    grRec.addEncodedQuery(QUERY);
    grRec.query();
    if (!grRec.hasNext()) {
        gs.info('YTT: [Generate YTT Transcripts] completed with ' + currentNumber +
            ' total records processed.');
        return; //No more records to process. Halt function, and loop.
    }

    while (grRec.next()) {
        var transcript = '';

        try {
            transcript = new x_792082_ytt.YttUtils().getTranscriptPerVideoID(grRec.getValue('video_id'));
            grRec.setValue('transcript', transcript);
            grRec.update();
            transcript = '';

            currentNumber++;
        } catch (e) {
            gs.error('YTT: [Generate YTT Transcripts] Error in processing Video ID ' + grRec.getValue('video_id') +
                '. Error: ' + e);
        }

    }

    //Log a progress update
    gs.info('YTT: [Generate YTT Transcripts] event wrapper processed ' + currentNumber +
        ' records so far. Triggering the next loop.');

    //Trigger the event again
    gs.eventQueue(EVENT_NAME, grRec, limit, currentNumber);
}

eventWrapper(event.parm1, event.parm2);
